import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createCategory, deleteCategory, getCategories } from "../../action/CategoryAction";
import style from '../style/Category.module.css'

const CategoryPage = () => {
    let [titleOfCategory, setTitle] = useState('')
    const dispatch = useDispatch()
    const categorySelector = useSelector(state => state.Category)

    useEffect( () => {
        dispatch(getCategories())
    }, [dispatch])
    let category = categorySelector.categories
    if (category === undefined) {
        category = []
    }
    else {
        return (
            <>
                <div className={style.CategoryDiv}>
                    {category.map( (categor) => 
                        <div key={categor.id}>
                            <p>{categor.title}</p>
                            <button onClick={ () => {dispatch(deleteCategory(categor.id))}}>DeleteCategory</button>
                        </div>
                    )}
                    <p>Create category</p>
                    <input autoFocus placeholder="Enter title of new category"
                    value={titleOfCategory} onChange = { e => setTitle(e.target.value)}/>
                    <button onClick={ () => {
                        dispatch(createCategory(titleOfCategory))
                    }}>Create Category</button>
                </div>
            </>
        )
    }
}

export default CategoryPage
