import React, {useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { GetLikes } from "../../../action/PostAction";
import { CSSTransition } from "react-transition-group";
import '../../style/PostPage.module.css'

const GetLike = (props) => {
    let {id,likes} = props
    const dispatch = useDispatch()
    useEffect (()=> {
        dispatch(GetLikes(id))
    }, [dispatch])
    if (likes === undefined) {
        likes = []
    }    
    return (
        <CSSTransition
            timeout={500}
            classNames="item">
            <b style={{marginLeft:"70%"}}>{likes.length}</b>
        </CSSTransition>
    )   
}

export default GetLike
