import { useDispatch, useSelector } from "react-redux";
import { getUser_by_id } from "../../../action/AuthAction";

const User = (props) => {
    const {id} = props
    let [user, setUser] = useState()
    const dispatch = useDispatch()
    const data = useSelector(state => state.Auth)
    useEffect( () => {  
        dispatch(getUser_by_id(id))
    },[dispatch])
    if (user === undefined) {
        user = []
    }
    user = data
    console.log(data);
    return (
        <>
        {user.map( (usr) => {
            return (
                <div key={id}>{usr.login}</div>    
            )
        }
        )}
        </>
    )

}

export default User;
