import { createCat, getCategory } from "../api/CategoryApi"

export const getCategories = () => async(dispatch) => {
    try {
        const {data} = await getCategory()
        return dispatch({type:'getCategory', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const createCategory = (title) => async(dispatch) => {
    try {
        const {data} = await createCat(title) 
        if (data.result.affectedRows > 0) {
            const Data = await getCategory()
            return dispatch({type:'createCategory'})
        }
    } catch (error) {
        console.log(error);
    }
}
