import { deleteComment, PatchComment } from "../api/CommentsApi";
import { createCommentsOnPost, createlike, CreatePost, deletelike, getCommentsOnPost, getLikesFromPost, getPost, PostByid, PostDelete } from "../api/PostApi"


export const getPosts = () => async(dispatch) => {
    try {
        const {data}= await getPost
        return dispatch({type:'getposts', payload:data})
    } catch (error) {
        console.log(error);
    }
}

export const GetLikes = (id) => async(dispatch) => {
    try {
        const {data} = await getLikesFromPost(id)
        return dispatch({type:'getlikes', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const getPostById = (id) => async(dispatch) => {
    try {
        const {data} = await PostByid(id)
        return dispatch({type:"getpostbyid", payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const getComments = (id) => async(dispatch) => {
    try {
        const {data} = await getCommentsOnPost(id)
        return dispatch({type:'getCommentsOnPost', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const createComment = (id, comment) => async(dispatch) => {
    try {
        const {data} = await createCommentsOnPost(id,comment)
        console.log(data);

        if (data.result.affectedRows > 0) {
            const Data = await getCommentsOnPost(id)
            if (Data.data.length === 0) {
                return dispatch({type:'createcommentforpost', payload:[]})
            }
            return dispatch({type:'createcommentforpost', payload:Data.data.result})
        }
    } catch (error) {
        console.log(error);
    }
}

export const createPost = (title,info,category) => async(dispatch) => {
    try {
        const {data} = await CreatePost(title,info,category)
        if (data.result.affectedRows > 0) {
            const Data = await getPost
            return dispatch({type:'createpost', payload:Data.data})
        }
    } catch (error) {
        console.log(error);
    }
}

export const likePost = (id) => async(dispatch) => {
    try {
        const {data} = await createlike(id)
        let likes
        if (data.result.affectedRows > 0) {
            const Data = await getLikesFromPost(id)
            dispatch({type:"likepost", payload:Data.data.result})
        }
    } catch (error) {
        alert('You already paste like')
    }
}

export const DeletComment = (id,post_id) => async(dispatch) => {
    try {
        const Data = await getCommentsOnPost(post_id)
        if (Data.data.result.length === 1) {
            const {data} = await deleteComment(id)
            if (data.result.affectedRows > 0) {
                return dispatch({type:'deletecomment',payload:[]})
            }   
        }
        else {
            const {data} = await deleteComment(id)
            if (data.result.affectedRows > 0) {
                const Comment = await getCommentsOnPost(post_id)
                return dispatch({type:'deletecomment',payload:Comment.data.result})
            }   
        }

    } catch (error) {
        console.log(error);
    }
}

export const deletePost = (id) => async(dispatch) => {
    try {
        const {data} = await PostDelete(id) 
        if (data.message.affectedRows > 0) {
            const Data = await getPost
            const allPost = Data.data
            let i
            for (let index = 0; index < allPost.length; index++) {
                if (allPost[index].id === id) {
                    i = index
                }
            }
            if (i > -1) {
                allPost.splice(i,1)
            }
            console.log(allPost);
            return dispatch({type:'deletepost', payload:allPost})
        }
    } catch (error) {

    }
}

export const UpdateComment = (id,post_id,content) => async(dispatch) => {
    try {
        const {data} = await PatchComment(id,content)
        if (data.result.affectedRows > 0) {
            const Data = await getCommentsOnPost(post_id)
            return dispatch({type:'updatecomment', payload:Data.data.result})
        }
    } catch (error) {
        alert("It`s not your comment")
    }
}

export const deleteLikeFromPost = (id) => async(dispatch) => {
    try {
        const {data} = await deletelike(id)
        if (data.result.affectedRows>0) {
            const Data = await getLikesFromPost(id)            
            dispatch({type:'deletelike', payload:Data.data.result})
        }
    } catch (error) {
        console.log(error);
    }
}
