import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";

import { getPosts } from "../../action/PostAction";
import User from "./Post/Getuser";

const HomePage = () => {
    let [posts, setPost] = useState()    
    const dispatch = useDispatch()
    const data = useSelector(state=>state.Post)
    useEffect (() => {
        dispatch(getPosts())
    },[dispatch])
    posts = data.allPost
    if (posts === undefined) {
        posts = []
    }
    console.log(posts);
    return (
        <>
            <div>
                {posts.map((pst) =>
                    <div key={pst.id}>
                        <div key={pst.id}><User id={pst.user_id}/></div>
                        <p>{pst.title}</p>
                        <p>{pst.info}</p>
                        <p>{pst.time.substring(0,10)}</p>
                    </div>
                )}

            </div>
        </>
    )
}
export default HomePage
