import React, {useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { GetLikes, getPostById } from "../../action/PostAction";
import { Link } from "react-router-dom";

import style from "../style/Homepage.module.css"
import GetLike from "./PostComponents/GetLikes";

const PostPage = () => {
    const {id} = useParams()
    let post, likes
    const dispatch = useDispatch()
    const data = useSelector(state => state.Post)
    useEffect ( () => {
        dispatch(getPostById(id))
        dispatch(GetLikes(id))
    }, [dispatch])
    console.log(data);
    post = data.post
    likes = data.like
    if (likes === undefined) {
        likes = []
    }
    if (post === undefined) {
        post = []
    }
    else {
        return (
            <>
                <div className={style.divAllPosts}>
                        <div className ={style.divPost} key={post[0].id}>
                            <Link className={style.loginHome} to ="/user/${pst.user_id}">{post[0].login}</Link>
                            <p className={style.titlePost}>Title: {post[0].title}</p>
                            <p className={style.contentPost}>{post[0].info}</p>
                            <p className={style.timePost}>{post[0].time.substring(0,10)}</p>
                            <p>{likes.length}</p>
                        </div>
                </div>
            </>
        );   
    }
}

export default PostPage;
