import { deleteComment } from "../api/CommentsApi"


export const DeleteComment = (id) => async(dispatch) => {
    try {
        const {data} = await deleteComment(id)
        return dispatch({type:'deletecomment', payload:data})
    } catch (error) {
        console.log(error);
    }   
}
