import axios from "axios";
import Cookies from "js-cookie";

const api = axios.create({
    baseURL:"http://127.0.0.1:3001/api"
})
const authIntreceptors = config => {
    config.headers.authorization = `Bearer ${Cookies.get('token')}` 
    return config
}
api.interceptors.request.use(authIntreceptors)

export const getCategory = () => api.get('/categories')
export const createCat = (title) => api.post('/categories', {title: title})
export const delCategory = (id) => api.delete(`categories/${id}`)
