export const initialState = {
    allPost:[],
    likes: [],
    post : [],
    comments: [],
}

export const PostReducer = (post = [],action) => {
    switch (action.type) {
        case 'getposts':
            return {...post,allPost:action.payload}
        case `getlikes`:
            return {...post,likes:action.payload}
        case 'getpostbyid':
            return {...post,post:action.payload}
        case `getCommentsOnPost`:
            return {...post, comments:action.payload}
        default:
            return post
    }
}
