import React, { useEffect,useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getPosts } from "../../action/PostAction";
import { getUser } from "../../action/UserAction";
import jwt_decode from 'jwt-decode'


const UserPage = () => {
    const {id} = useParams()
    let [login, setLogin] = useState('')
    let [email, setEmail] = useState('')
    let [full_name, setFull_name] = useState('')
    const data = useSelector(state=>state.User)
    const post = useSelector(store => store.Post)
    const auth = useSelector(state=>state.Auth)
    let [index, setIndex] = useState(0)

    const token = auth.token
    const decode = jwt_decode(token)
    const user_id = decode.id

    const dispatch = useDispatch()
    useEffect( () => {
        dispatch(getUser(id))
        dispatch(getPosts())
    }, [dispatch])
    let user = data.user
    let allPost = post.allPost 

    if (allPost === undefined) {
        allPost = []
    }
    if (user.length === 0) {
        <div>Loading</div>
    }
    else {
        return (
            <div>
                {user_id === user[0].id ? 
                    <>
                        <div>
                            <p>Full name: {user[0].full_name}</p>
                            <p>Login: {user[0].login}</p>
                            <p>Email: {user[0].email}</p>
                            <p>User: {user[0].role}</p>
                            {index === 1 ? 
                                <>
                                    <input type='text' placeholder="Enter new login" value={login} onChange={ e => setLogin(e.target.value)}></input>
                                    <input type='email' placeholder="Enter new email" value={email} onChange={ e => setEmail(e.target.value)}></input>
                                    <input type='text' placeholder="Enter new full name" value={full_name} onChange={ e => setFull_name(e.target.value)}></input>
                                </> 
                                : 
                                <></>}
                            <button onClick={ () => {
                                if (index === 0) {
                                    setIndex(1)
                                }
                                if (index === 1) {
                                    setIndex(0)
                                }
                            }}>Update</button>
                        </div>
                        <div>
                            <p>Posts:</p>
                            {allPost.map( (posts) => 
                                <div key={posts.id}> 
                                    {posts.user_id === user[0].id ?
                                        <>
                                            <p>{posts.title}</p>
                                            <p>{posts.info}</p>
                                            <p>{posts.time}</p>
                                            <button>Delete</button> 
                                        </> 
                                        :
                                        <></>
                                    }
                                </div>
                            )}
                        </div>
                    </>
                    :
                    <>
                        <div>
                            <p>Full name: {user[0].full_name}</p>
                            <p>Login: {user[0].login}</p>
                            <p>Email: {user[0].email}</p>
                            <p>User: {user[0].role}</p>
                        </div>
                        <div>
                            <p>Posts:</p>
                            {allPost.map( (posts) => 
                                <div key={posts.id}> 
                                    {posts.user_id === user[0].id ? 
                                        <>
                                            <p>{posts.title}</p>
                                            <p>{posts.info}</p>
                                            <p>{posts.time}</p>
                                        </>
                                        :
                                        <></>
                                    }
                                </div>
                            )}
                        </div>
                    </>
                }
            </div>
        )
    }
}

export default UserPage
