import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import style from "../../style/Homepage.module.css"
import GetLike from "./GetLikes";

const Post = (props) => {
    let {post_id, title, info, like, time, user_login} = props
    return (
        <>
            <div className={style.divAllPosts}>
                <div className ={style.divPost} key={post_id}>
                        <Link className={style.loginHome} to ="/user/${pst.user_id}">{user_login}</Link>
                        <p className={style.titlePost}>Title: {title}</p>
                        <p className={style.contentPost}>{info}</p>
                        <p className={style.timePost}>{time.substring(0,10)}</p>
                        <div><GetLike post_id = {post_id} likes = {like}/></div><button className={style.buttonLikePost}>LIKE</button>
                        <button className={style.buttonCommentPost}>COMMENTS</button>
                </div>

            </div>
        </>
    )
}
export default Post
