import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getPosts } from "../../action/PostAction";
import style from "../style/Homepage.module.css"

const HomePage = () => {
    let [posts, setPost] = useState()    
    let [likes,setLike] = useState(1)
    const dispatch = useDispatch()
    const data = useSelector(state => state.Post)
    useEffect (() => {
        dispatch(getPosts())
    },[dispatch])
    posts = data.allPost
    if (posts === undefined) {
        posts = []
    }
    console.log(posts);
    return (
        <>
            <div className={style.divAllPosts}>
                {posts.map((pst) =>
                    <div className ={style.divPost} key={pst.id}>
                        <Link className={style.loginHome}>{pst.login}</Link>
                        <p className={style.titlePost}>Title: {pst.title}</p>
                        <p className={style.contentPost}>{pst.info}</p>
                        <p className={style.timePost}>{pst.time.substring(0,10)}</p>
                        <a>{likes}<button className={style.buttonLikePost} onClick={()=>{setLike(likes+1)}}>LIKE</button></a>
                        <button className={style.buttonCommentPost}>COMMENTS</button>
                    </div>
                )}

            </div>
        </>
    )
}
export default HomePage
