import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getPosts } from "../../action/PostAction";
import { getUser } from "../../action/UserAction";
import jwt_decode from 'jwt-decode'


const UserPage = () => {
    const {id} = useParams()
    const data = useSelector(state=>state.User)
    const post = useSelector(store => store.Post)
    const auth = useSelector(state=>state.Auth)
    const token = auth.token
    const decode = jwt_decode(token)
    const user_id = decode.id
    const dispatch = useDispatch()
    useEffect( () => {
        dispatch(getUser(id))
        dispatch(getPosts())
    }, [dispatch])
    console.log(data.user);
    let UserPosts 
    let user = data.user
    let allPost = post.allPost    
    if (allPost === undefined) {
        allPost = []
    }
    for (let index = 0; index <allPost.length; index++) {
        if (allPost[index].user_id === id) {
            UserPosts = allPost[index]
        }
    }
    console.log(UserPosts);
    if (user.length === 0) {
        <div>Loading</div>
    }
    else {
        return (
            <div>
                {user_id === user[0].id ? 
                    <>
                        <div>
                            <p>Full name: {user[0].full_name}</p>
                            <p>Login: {user[0].login}</p>
                            <p>Email: {user[0].email}</p>
                            <p>User: {user[0].role}</p>
                            <button>Update</button>
                        </div>
                        <div>
                            <p>Posts:</p>
                            {allPost.map( (posts) => 
                                <div key={posts.id}> 
                                    {posts.user_id === user[0].id ?
                                        <>
                                            <p>{posts.title}</p>
                                            <p>{posts.content}</p>
                                            <button>Delete</button> 
                                        </> 
                                        :
                                        <></>
                                    }
                                </div>
                            )}
                        </div>
                    </>
                    :
                    <>
                        <div>
                            <p>Full name: {user[0].full_name}</p>
                            <p>Login: {user[0].login}</p>
                            <p>Email: {user[0].email}</p>
                            <p>User: {user[0].role}</p>
                        </div>
                        <div>
                            <p>Posts:</p>
                            {allPost.map( (posts) => 
                                <div key={posts.id}> 
                                    {posts.user_id === user[0].id ? 
                                        <p>{posts.title}</p> 
                                        :
                                        <></>
                                    }
                                </div>
                            )}
                        </div>
                    </>
                }
            </div>
        )
    }
}

export default UserPage
