import React, { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useHistory } from "react-router-dom"
import { getAllUsers } from "../../action/UserAction"


const AllUsers = () => {
    const dispatch = useDispatch()
    const history = useHistory()
    const usersSelector = useSelector(store => store.User)
    const auth = useSelector(state => state.Auth)
    let allUsers

    const token = auth.token
    const decode = jwt_decode(token)
    const decoded_id = decode.id
    useEffect ( () => {
        dispatch(getAllUsers())       
    }, [dispatch])
    allUsers = usersSelector.allUsers
    if (allUsers === undefined) {
        allUsers = []
    }
    else {
        return (
            <>
                <div>
                    {allUsers.map( (user) => 
                        <div key={user.id} onClick = { () => {history.push(`/user/${user.id}`)}}>
                            <img src={`http://localhost:3001/avatar/${user.photo}`} alt='avatar' width='100 px'/>
                            <p>{user.login}</p>
                            <p>{user.email}</p>
                            <p>{user.full_name}</p>
                        </div>
                    )}
                </div>
            </>
        )
    }
}

export default AllUsers
