import moment from "moment";
import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { DeletComment, getComments, UpdateComment } from "../../../action/PostAction";

import style from '../../style/PostPage.module.css'

const CommentsForPost = (props) => {
    let [index, setIndex] = useState()
    const {post_id,comments} = props
    const dispatch = useDispatch()
    useEffect ( () => {
        dispatch(getComments(post_id))
    }, [dispatch])
    if (comments === undefined) {
        comments = []
    }
    if (comments.length === 0) {
        return (
            <>No Comments yet</>
        )
    }
    else {
        return (
            <>
            {comments.map( (cmnt) => 
                <div key={cmnt.id} className={style.Comment}>
                    <p className={style.Commentlogin}>{cmnt.login}</p>
                    <p className={style.Commentcontent}>{cmnt.content}</p>
                    <button onClick= { () => {
                        dispatch(UpdateComment(cmnt.id,post_id, "jopa"))
                    }}>Edit</button>
                    <button onClick={() => {
                        dispatch(DeletComment(cmnt.id,post_id))
                    }}>Delete</button>
                    <p className={style.Commenttime}>{moment(cmnt.time).fromNow()}</p>
                </div>
            )}
            </>
        )
    }
}

export default  CommentsForPost
