import React, { useState } from 'react'
import { useSelector } from 'react-redux';
import moment from 'moment';
import jwt_decode from 'jwt-decode'
import ShareIcon from '@mui/icons-material/Share';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

import { SketchPicker, ChromePicker } from 'react-color';
import { CreateEvent, DeleteEvent } from '../../action/EventAction';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import { useEffect } from 'react';
import { deleteEvent } from '../../api/EventApi';


const EventView = ({ event, setVisible }) => {
    const [isEdit, setIsEdit] = useState(false);
    const [titleEdit, setTitleEdit] = useState();
    const [descriptionEdit, setDescriptionEdit] = useState();
    const [colorEdit, setColorEdit] = useState();
    const [timeEdit, setTimeEdit] = useState("");
    const [timeStartEdit, setStartTimeEdit] = useState("");
    const [timeEndEdit, setEndTimeEdit] = useState("");
    const [errorMessage, setMessage] = useState('');
    const [categoryEdit, setCategoryEdit] = useState('');
    const auth = useSelector(state => state.Auth)
    const tokenn = auth.token
    let user_id, decode;

    useEffect(() => {
        //
        setIsEdit(false)
        if (event.type) {
            setTitleEdit(event.title)
            setDescriptionEdit(event.description)
            setColorEdit(event.color)
            setCategoryEdit(event.type)
            if (event.type === "arrangement") {
                setEndTimeEdit(moment(event.end_At).format('HH:mm'))
                setStartTimeEdit(moment(event.start_At).format('HH:mm'))
            }
            else {
                setTimeEdit(moment(event.start_At).format('HH:mm'))
            }
        }
    }, [event])
    const setEdit = (e) =>{
        e.preventDefault();
        isEdit 
        ? 
            setIsEdit(false) 
        : 
            setIsEdit(true)
    }


    if (tokenn === '') {

    }
    else {
        decode = jwt_decode(tokenn)
        user_id = decode.id
    }

    if (event === undefined) {
        event = []
    }
    else {
        console.log(event);
        return (
            <div>
                {isEdit 
                    ?
                        <form></form>
                    :
                        <form>
                            <h1 style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>Event {event.user_id === user_id ? <div><ShareIcon /> <DeleteIcon /> <EditIcon /></div> : <></>}</h1>
                            <p>Author: {event.user_id}</p>
                            <p>Title: {event.title}</p>
                            <p>Description: {event.description}</p>
                            <p>Type: {event.type}</p>
                            <p>Date: {moment(event.start_At).format('YYYY-MM-DD')}</p>
                            {event.type === 'arrangement' ?
                                <>
                                    <p>Started: {moment(event.start_At).format('HH:mm')}</p>
                                    <p>Finished: {moment(event.end_At).format('HH:mm')}</p>
                                </>
                                :
                                <>
                                    <p>Time: {moment(event.start_At).format('HH:mm')}</p>
                                </>
                            }
                        </form>
                }

            </div>

        )
    }
}

export default EventView;
