import React, { useContext, useEffect, useState } from "react";
import Cookies from 'js-cookie'
import style from "../style/Navbar.module.css"
import {observer} from 'mobx-react-lite'
import { Context } from "../..";
const Navbar= () => {
    const auth = useContext(Context)
    const [isAuth, setAuth] = useState()
    setAuth()
    const token = Cookies.get('token')
    useEffect ( () => {
        if (token) {
            setAuth(true)
        }
        else {
            setAuth(false)
        }      
    }, [])
    // if (token) {
    //     isAuth = true
    // }
    // else {
    //     isAuth = false
    // }

    console.log(isAuth);

    return (
        isAuth ? 
        <>
            <h1 className={style.h1Navbar}>homePage</h1>
            <div >
                <ul className={style.ulNavbar}> 
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/home">Home</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/category">Categories</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/post">+</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/user">User</a></li>
                    <li className={style.liNavbar}><button onClick={()=>{Cookies.remove('token')
                                                                        setAuth(false)}}>logout</button></li>

                </ul>

            </div>


        </>
        :
        <>
            <h1 className={style.h1Navbar}>homePage</h1>
            <div>
                <ul className={style.ulNavbar}>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/home">Home</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/category">Categories</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/post">+</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/register">Register</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/login">Login</a></li>
                </ul>
            </div>

        </>

    );
}


export default Navbar;
