import React, { useEffect,useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useParams } from "react-router-dom";
import { deletePost, getPosts, getPostsByCategory } from "../../action/PostAction";
import { getUser, UpdateAvatar, UpdateUser } from "../../action/UserAction";
import jwt_decode from 'jwt-decode'
import Pagination from "./Pagination/Pagination";
import { getCategories } from "../../action/CategoryAction";


const UserPage = () => {
    const {id} = useParams()
    const history = useHistory()
    let [login, setLogin] = useState('')
    let [email, setEmail] = useState('')
    let [full_name, setFull_name] = useState('')
    let [index, setIndex] = useState(0)
    let [indexOfAvatar, setIndexOfAvatar] = useState(0)
    let [file, setFile] = useState('')
    let [currentPage, setCurrentPage] = useState(1)
    let [postPerPage] = useState(5)

    const data = useSelector(state=>state.User)
    const post = useSelector(store => store.Post)
    const auth = useSelector(state=>state.Auth)
    const categorieSelector = useSelector(state => state.Category)

    const indexOfLastPost = currentPage * postPerPage
    const indexOfFirstPost = indexOfLastPost - postPerPage
    const token = auth.token
    const decode = jwt_decode(token)
    const user_id = decode.id

    const pagination = (pageNumber) => {setCurrentPage(pageNumber)}
    const dispatch = useDispatch()
    useEffect( () => {
        dispatch(getCategories())
        dispatch(getUser(id))
        dispatch(getPosts())
    }, [dispatch])

    let user = data.user
    let allPost = post.allPost 
    let categorie = categorieSelector.categories

    if (categorie === undefined) {
        categorie = []
    }
    if (allPost === undefined) {
        allPost = []
    }
    if (user.length === 0) {
        <div>Loading</div>
    }
    else {
        const CurrentPost = allPost.slice(indexOfFirstPost, indexOfLastPost)
        return (
            <div>
                {user_id === user[0].id ? 
                    <>
                        <div>
                            <img alt='avatar' src={`http://localhost:3001/avatar/${user[0].photo}`}/>
                            <button onClick={ () => {
                                if (indexOfAvatar === 0) {
                                    setIndexOfAvatar(1)
                                }
                                if (indexOfAvatar === 1) {
                                    setIndexOfAvatar(0)
                                    dispatch(UpdateAvatar(id, file))
                                }
                            }}>Update avatar</button>
                            {indexOfAvatar  === 1 ? 
                                <><input type="file" name="photo" onChange = { e => setFile(e.target.files[0])}/></>
                                :
                                <></>
                            }
                            <p>Full name: {user[0].full_name}</p>
                            <p>Login: {user[0].login}</p>
                            <p>Email: {user[0].email}</p>
                            <p>User: {user[0].role}</p>
                            {index === 1 ? 
                                <>
                                    <input type='text' placeholder="Enter new login" value={login} onChange={ e => setLogin(e.target.value)}></input>
                                    <input type='email' placeholder="Enter new email" value={email} onChange={ e => setEmail(e.target.value)}></input>
                                    <input type='text' placeholder="Enter new full name" value={full_name} onChange={ e => setFull_name(e.target.value)}></input>
                                </> 
                                : 
                                <></>}
                            <button onClick={ () => {
                                if (index === 0) {
                                    setIndex(1)
                                }
                                if (index === 1) {
                                    setIndex(0)
                                    dispatch(UpdateUser(id, login,email,full_name))
                                }
                            }}>Update</button>
                        </div>
                        <div>
                            <p>Posts:</p>
                            <div onChange={ e => dispatch({type:e.target.value, payload:allPost})}>
                                <input value={'getposts'} type='radio' name= 'sort' id='default'></input>
                                <label for='default'>The newest</label>
                                <input value={'sortbyold'} type='radio' name ='sort' id='old'></input>
                                <label for='old'>The oldest</label>
                            </div>
                            <div onChange={ e => dispatch(getPostsByCategory(e.target.value))}>
                                <p>Choose categories</p>
                                <input type ='radio' name = 'category' id = 'all' value = 'all' />
                                <label for='all'>All posts</label>
                                {categorie.map( (category) =>
                                    <>
                                        <input type ='radio' name = 'category' id = {category.id} value = {category.id} />
                                        <label for={category.id}>{category.title}</label>
                                    </>
                                )}
                            </div>
                            {CurrentPost.map( (posts) => 
                                <div key={posts.id}> 
                                    {posts.user_id === user[0].id ?
                                        <>
                                            <div onClick = { () => history.push(`/post/${posts.id}`)}>
                                                <p>{posts.title}</p>
                                                <p>{posts.info}</p>
                                                <p>{posts.time}</p>
                                            </div>
                                            <button onClick={ () => {dispatch(deletePost(posts.id))}}>Delete</button> 
                                        </> 
                                        :
                                        <></>
                                    }
                                </div>
                            )}
                            <Pagination paginate={pagination} postPerPage={postPerPage} totalPost ={allPost.length} />
                        </div>
                    </>
                    :
                    <>
                        <div>
                            <img alt='avatar' src={`http://localhost:3001/avatar/${user[0].photo}`}/>
                            <p>Full name: {user[0].full_name}</p>
                            <p>Login: {user[0].login}</p>
                            <p>Email: {user[0].email}</p>
                            <p>User: {user[0].role}</p>
                        </div>
                        <div>
                            <p>Posts:</p>
                            <div onChange={ e => dispatch({type:e.target.value, payload:allPost})}>
                                <input value={'getposts'} type='radio' name= 'sort' id='default'></input>
                                <label for='default'>The newest</label>
                                <input value={'sortbyold'} type='radio' name ='sort' id='old'></input>
                                <label for='old'>The oldest</label>
                            </div>
                            <div onChange={ e => dispatch(getPostsByCategory(e.target.value))}>
                                <p>Choose categories</p>
                                <input type ='radio' name = 'category' id = 'all' value = 'all' />
                                <label for='all'>All posts</label>
                                {categorie.map( (category) =>
                                    <>
                                        <input type ='radio' name = 'category' id = {category.id} value = {category.id} />
                                        <label for={category.id}>{category.title}</label>
                                    </>
                                )}
                            </div>
                            {CurrentPost.map( (posts) => 
                                <div key={posts.id} onClick = { () => history.push(`/post/${posts.id}`)}> 
                                    {posts.user_id === user[0].id ? 
                                        <>
                                            <p>{posts.title}</p>
                                            <p>{posts.info}</p>
                                            <p>{posts.time}</p>
                                        </>
                                        :
                                        <></>
                                    }
                                </div>
                            )}
                            <Pagination paginate={pagination} postPerPage={postPerPage} totalPost ={allPost.length}/>
                        </div>
                    </>
                }
            </div>
        )
    }
}

export default UserPage
