import React, { useEffect, useState } from "react";
import Cookies from 'js-cookie'
import style from "../style/Navbar.module.css"
import {observer} from 'mobx-react-lite'
import { useHistory } from "react-router-dom";
import jwt_decode from 'jwt-decode'
import { useSelector } from "react-redux";


const Navbar= () => {
    const history = useHistory()
    const [isAuth, setAuth] = useState()
    const auth = useSelector(state=>state.Auth)
    const tokenn = auth.token
    let decode 
    let user_id
    if (tokenn === '') {

    }
    else {
        decode = jwt_decode(tokenn)
        user_id = decode.id
    }
    const token = Cookies.get('token')   

    useEffect ( () => {
        if (token) {
            setAuth(true)
        }
        else {
            setAuth(false)
        }      
    }, [token])
    // if (token) {
    //     isAuth = true
    // }
    // else {
    //     isAuth = false
    // }
    return (
        isAuth ? 
        <>
            <h1 className={style.h1Navbar}>homePage</h1>
            <div >
                <ul className={style.ulNavbar}> 
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/home">Home</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/users">Users</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} onClick={()=> {history.push(`/user/${user_id}`)}}>{decode.login}</a></li>
                    <li className={style.liNavbar}><button onClick={()=>{Cookies.remove('token')
                                                                        setAuth(false)}}>logout</button></li>

                </ul>

            </div>


        </>
        :
        <>
            <h1 className={style.h1Navbar}>homePage</h1>
            <div>
                <ul className={style.ulNavbar}>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/home">Home</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/users">Categories</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/post">+</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/register">Register</a></li>
                    <li className={style.liNavbar}><a className={style.aNavbar} href="/login">Login</a></li>
                </ul>
            </div>

        </>

    );
}


export default Navbar;
