import { getPost } from "../api/PostApi"
import Cookies from "js-cookie";
export const getPosts = () => async(dispatch) => {
    try {
        const token = Cookies.get('token')
        const data = await getPost(token)

        console.log(data);
        dispatch({type:'getpost',payload:data})   

    } catch (error) {
        alert(error)
    }
}
