import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUser_by_id } from "../../../action/AuthAction";

const User = (props) => {
    const {id} = props
    const dispatch = useDispatch()
    const data = useSelector(state = state.Auth)
    useEffect( () => {  
        dispatch(getUser_by_id(id))
    },[dispatch])
    let user = data
    console.log(user);    
    return (
        <div>{user}</div>
    )
}

export default User;
