import React from "react";

export default class UserStore {
    constructor() {
        this._isAuth = false;
        this._user = {}
    }
    setIsAuth(bool) {
        this._isAuth = bool
    }
    setIsAuth(user) {
        this._isAuth = user
    }
    get isAuth() {return this._isAuth}
    get user () {return this._user}
}
