import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useParams } from "react-router-dom";
import { createComment, deleteLikeFromPost, GetLikes, getPostById, likePost, UpdatePost } from "../../action/PostAction";
import { Link } from "react-router-dom";
import moment from 'moment'
import style from "../style/PostPage.module.css"
import GetLike from "./PostComponents/GetLikes";
import CommentsForPost from "./PostComponents/GetComment";
import Category from "./PostComponents/GetCategory";
import jwt_decode from 'jwt-decode'

const PostPage = () => {
    const [comment, setComment] = useState('')
    let [info, setInfo] = useState('')
    let [title, setTitle] = useState('')
    let [category, setCategory] = useState('')
    const [check, setCheck] = useState(false)
    let [index, setIndex] = useState(0)
    const {id} = useParams()
    let post, likes, comments, categories

    const history = useHistory()
    const dispatch = useDispatch()
    const data = useSelector(state => state.Post)
    const auth = useSelector(state=>state.Auth)
    const categorySelector = useSelector(state => state.Category)

    const token = auth.token
    const decode = jwt_decode(token)
    const decoded_id = decode.id

    useEffect ( () => {
        dispatch(getPostById(id))
        dispatch(getCategories())

    }, [dispatch])
    post = data.post
    likes = data.likes
    comments = data.comments
    categories = categorySelector.categories

    const like = () => {
        if (check === false) {
            setCheck(true)
            dispatch(likePost(id))
        }
        else if (check === true) {
            setCheck(false)
            dispatch(deleteLikeFromPost(id))
        }
    }
    const create = (e) => {
        e.preventDefault()
        dispatch(createComment(id, comment))
    }
    if (categories === undefined) {
        categories = []
    }
    if (comments === undefined) {
        comments = []
    }
    if (likes === undefined) {
        likes = []
    }
    if (post === undefined) {
        post = []
    }
    else {
        return (
            <>
                {post[0].user_id === decoded_id ? 
                    <>
                        <div className={style.divAllPosts}>
                            <div className ={style.divPost} key={post[0].id}>
                                <a className={style.loginHome} onClick = {() => {history.push(`/user/${post[0].user_id}`)}}>{post[0].login}</a>
                                <p className={style.titlePost}>Title: {post[0].title}</p>
                                <p className={style.contentPost}>{post[0].info}</p>
                                <Category post_id = {id} />
                                <button onClick={ () => {
                                    if (index === 0) {
                                        setIndex(1)
                                    }
                                    if (index === 1) {
                                        setIndex(0)
                                        dispatch(UpdatePost(post[0].id, info, title, category))
                                    }
                                }}>Edit</button>
                                {index === 1 ? 
                                    <>
                                        <input type='text' name = 'title' 
                                               placeholder="Enter new title" required = {true}
                                               value = {title} onChange = { e => setTitle(e.target.value)}> 
                                        </input>
                                        <input type='text' name = 'info' 
                                               placeholder="Enter new content" required = {true}
                                               value = {info} onChange = { e => setInfo(e.target.value)}> 
                                        </input>
                                        <select onChange={ e => setCategory(e.target.value)}>
                                            <option value=''>Select category of post</option>
                                            {categories.map( (categor) => 
                                                <option value={categor.title} key={categor.id}>{categor.title}</option>
                                            )}
                                        </select>
                                    </>
                                    :
                                    <></>
                                }
                                <GetLike id = {id} likes = {likes}/><button onClick={like}>Like</button>
                                <p className={style.timePost}>{moment(post[0].time).fromNow()}</p>
                            </div>
                            <div className={style.divComments}>
                                <p className={style.headerComments}>Comments</p>
                                <CommentsForPost post_id = {post[0].id} comments ={comments}/>
                                <input 
                                    className={style.Commentinput}
                                    name="comment" type='text' 
                                    placeholder="Leave comment if you want" 
                                    maxLength={255} 
                                    value={comment} onChange = {e => setComment(e.target.value)}>
                                </input>
                                <button onClick={create} className={style.CommentCreateButton}>Create comment</button>
                            </div>
                        </div>
                    </>    
                    :
                    <>
                        <div className={style.divAllPosts}>
                            <div className ={style.divPost} key={post[0].id}>
                                <a className={style.loginHome} onClick = {() => {history.push(`/user/${post[0].user_id}`)}}>{post[0].login}</a>
                                <p className={style.titlePost}>Title: {post[0].title}</p>
                                <p className={style.contentPost}>{post[0].info}</p>
                                <Category post_id = {id} />
                                <GetLike id = {id} likes = {likes}/><button onClick={like}>Like</button>
                                <p className={style.timePost}>{moment(post[0].time).fromNow()}</p>
                            </div>
                            <div className={style.divComments}>
                                <p className={style.headerComments}>Comments</p>
                                <CommentsForPost post_id = {post[0].id} comments ={comments}/>
                                <input 
                                    className={style.Commentinput}
                                    name="comment" type='text' 
                                    placeholder="Leave comment if you want" 
                                    maxLength={255} 
                                    value={comment} onChange = {e => setComment(e.target.value)}>
                                </input>
                                <button onClick={create} className={style.CommentCreateButton}>Create comment</button>
                            </div>
                        </div>
                    </>
                }

            </>
        );   
    }
}

export default PostPage;
