import React, { useEffect,useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useParams } from "react-router-dom";
import { deletePost, getPosts, getPostsByCategory } from "../../action/PostAction";
import { getRatingUser, getUser, UpdateAvatar, UpdateUser } from "../../action/UserAction";
import jwt_decode from 'jwt-decode'
import Pagination from "./Pagination/Pagination";
import { getCategories } from "../../action/CategoryAction";
import moment from 'moment'
import style from '../style/UserPage/UserPage.module.css'


const UserPage = () => {
    const {id} = useParams()
    const history = useHistory()
    let [login, setLogin] = useState('')
    let [email, setEmail] = useState('')
    let [full_name, setFull_name] = useState('')
    let [index, setIndex] = useState(0)
    let [index_Of_Login, setIndexOfLogin] = useState(0)
    let [index_Of_Email, setIndexOfEmail] = useState(0)
    let [indexOfAvatar, setIndexOfAvatar] = useState(0)
    let [file, setFile] = useState('')
    let [currentPage, setCurrentPage] = useState(1)
    let [postPerPage] = useState(5)

    const data = useSelector(state=>state.User)
    const post = useSelector(store => store.Post)
    const auth = useSelector(state=>state.Auth)
    const categorieSelector = useSelector(state => state.Category)

    const indexOfLastPost = currentPage * postPerPage
    const indexOfFirstPost = indexOfLastPost - postPerPage
    const token = auth.token
    const decode = jwt_decode(token)
    const user_id = decode.id

    const pagination = (pageNumber) => {setCurrentPage(pageNumber)}
    const dispatch = useDispatch()
    useEffect( () => {
        dispatch(getRatingUser(id))
        dispatch(getCategories())
        dispatch(getUser(id))
        dispatch(getPosts())
    }, [dispatch])

    let user = data.user
    let allPost = post.allPost 
    let categorie = categorieSelector.categories
    let rating = data.rating

    if (rating === undefined) {
        rating = []
    }
    if (categorie === undefined) {
        categorie = []
    }
    if (allPost === undefined) {
        allPost = []
    }
    if (user.length === 0) {
        <div>Loading</div>
    }
    else {
        const CurrentPost = allPost.slice(indexOfFirstPost, indexOfLastPost)
        return (
            <div className={style.MainDivUser}>
                {user_id === user[0].id ? 
                    <>
                        <div className={style.UserDiv}>
                            <img alt='avatar' src={`http://localhost:3001/avatar/${user[0].photo}`}/>
                            <button onClick={ () => {
                                if (indexOfAvatar === 0) {
                                    setIndexOfAvatar(1)
                                }
                                if (indexOfAvatar === 1) {
                                    setIndexOfAvatar(0)
                                    dispatch(UpdateAvatar(id, file))
                                }
                            }} className={style.ButtonUserAvatar}>Update avatar</button>
                            {indexOfAvatar  === 1 ? 
                                <><input type="file" name="photo" onChange = { e => setFile(e.target.files[0])}/></>
                                :
                                <></>
                            }
                            <p>Full name: {index === 1 ? <input type='text' placeholder="Enter new full name" value={full_name} onChange={ e => setFull_name(e.target.value)}></input>
                            : 
                            user[0].full_name}
                            <button onClick={ () => {
                                if (index === 0) {
                                    setIndex(1)
                                }
                                if (index === 1) {
                                    setIndex(0)
                                    dispatch(UpdateUser(id, login,email,full_name))
                                }
                            }}>Update</button>
                            </p>
                            <p>Login: {index_Of_Login === 1 ?  
                            <input type='text' placeholder="Enter new login" value={login} onChange={ e => setLogin(e.target.value)}></input>
                            : user[0].login}
                            <button onClick={ () => {
                                if (index_Of_Login === 0) {
                                    setIndexOfLogin(1)
                                }
                                if (index_Of_Login === 1) {
                                    setIndexOfLogin(0)
                                    dispatch(UpdateUser(id, login,email,full_name))
                                }
                            }}>Update</button>
                            </p>
                            <p>Email: {index_Of_Email === 1 ? 
                            <input type='email' placeholder="Enter new email" value={email} onChange={ e => setEmail(e.target.value)}></input>
                            : 
                            user[0].email}
                            <button onClick={ () => {
                                if (index_Of_Email === 0) {
                                    setIndexOfEmail(1)
                                }
                                if (index_Of_Email === 1) {
                                    setIndexOfEmail(0)
                                    dispatch(UpdateUser(id, login,email,full_name))
                                }
                            }}>Update</button>
                            </p>
                            <p>Role: {user[0].role}</p>
                            <p>Rating: {rating.length}</p>
                            {index === 1 ? 
                                <>
                                </> 
