import React, { useState } from "react";
import Calendar from 'react-calendar'


const HomePage = () => {
    const [date, setDate] = useState(new Date())
    console.log(date);
    return (
     <div className="app">
       <h1 className="header">Calendar</h1>
       <div className="calendar-container">
         <Calendar 
          onChange={setDate}
          value={date}
          maxDate={new Date()}
          formatDay ={
            (date) => new Intl.DateTimeFormat(

              {
                year: "numeric", 
                month: "numeric", 
                day: "numeric"
              }).format(date)
            }
         />
       </div>
       <div className="text-center">
          Selected date: {date.toLocaleDateString({year:"numeric", month:'numeric', day:'numeric'})}
       </div>
     </div>
      )
}

export default HomePage;
