import React, { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useHistory } from "react-router-dom"
import { deleteUser, getAllUsers } from "../../action/UserAction"
import jwt_decode from 'jwt-decode'
import Pagination from "./Pagination/Pagination"
import style from '../style/AllUsers.module.css'


const AllUsers = () => {
    const dispatch = useDispatch()
    const history = useHistory()
    const usersSelector = useSelector(store => store.User)
    const auth = useSelector(state => state.Auth)
    let Users, role
    let [currentPage, setCurrentPage] = useState(1)
    let [postPerPage] = useState(5)

    const indexOfLastPost = currentPage * postPerPage
    const indexOfFirstPost = indexOfLastPost - postPerPage

    const token = auth.token
    const decode = jwt_decode(token)
    role = decode.role
    const pagination = (pageNumber) => {setCurrentPage(pageNumber)}
    useEffect ( () => {
        dispatch(getAllUsers())       
    }, [dispatch])
    Users = usersSelector.allUsers
    if (Users === undefined) {
        Users = []
    }
    else {
        const allUsers = Users.slice(indexOfFirstPost, indexOfLastPost)
        return (
            <>
                {role === 'admin' ?
                    <> 
                    <div className={style.allUsersDiv}>

                        {allUsers.map( (user) => 
                            <div key={user.id} className={style.UserDiv}>
                                <div key={user.id} onClick = { () => {history.push(`/user/${user.id}`)}}>
                                    <img src={`http://localhost:3001/avatar/${user.photo}`} alt='avatar' width='100 px'/>
                                </div>
                                <p>{user.login}</p>
                                    <p>{user.email}</p>
                                    <p>{user.full_name}</p>
                                <button onClick={ () => dispatch(deleteUser(user.id))}>Delete</button>
                            </div>
                        )}
                    </div>
                    <Pagination paginate={pagination} postPerPage={postPerPage} totalPost ={Users.length} />
                    </>
                :
                <div>
                    {allUsers.map( (user) => 
                        <div key={user.id} onClick = { () => {history.push(`/user/${user.id}`)}}>
                            <img src={`http://localhost:3001/avatar/${user.photo}`} alt='avatar' width='100 px'/>
                            <p>{user.login}</p>
                            <p>{user.email}</p>
                            <p>{user.full_name}</p>
                        </div>
                    )}
                    <Pagination paginate={pagination} postPerPage={postPerPage} totalPost ={Users.length} />
                </div>
                }
            </>
        )
    }
}

export default AllUsers
