import axios from "axios";
import { getLikesFromPost, getPost } from "../api/PostApi"
import api from "../api/PostApi";
export const getPosts = (id) => async(dispatch) => {
    try {
        const {data}= await getPost
        return dispatch({type:'getposts', payload:data})
    } catch (error) {
        console.log(error);
    }
}

export const GetLikes = (id) => async(dispatch) => {
    try {
        const data = await getLikesFromPost(id)
        return dispatch({type:'getlikes', payload:data})
    } catch (error) {
        console.log(error);
    }
}
