import Cookies from "js-cookie";
import { getPost } from "../api/PostApi"

export const getPosts = ()=> async(dispatch) => {
    try {
        const {data} = await getPost()
        Cookies.set('token', Cookies.get('token'))
        console.log(data);
        dispatch({type:'getPost',payload:data})   
    } catch (error) {
        alert(error)
    }
}
