import jwt_decode from "jwt-decode";
import moment from "moment";
import React, { createElement, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { DeletComment, getComments, getLikesFromComment, UpdateComment } from "../../../action/PostAction";

import style from '../../style/PostPage.module.css'

const CommentsForPost = (props) => {
    const history = useHistory()
    let [comment, setComment] = useState('')
    let [index, setIndex] = useState(0)
    let [indexOfLike, setIndexOfLike] = useState(0)

    const auth = useSelector(state=>state.Auth)
    const commentSelector = useSelector(state => state.Comment)
    const token = auth.token
    const decode = jwt_decode(token)
    const user_id = decode.id
    const {post_id,comments} = props

    const dispatch = useDispatch()
    useEffect ( () => {
        dispatch(getComments(post_id))
    }, [dispatch])
    let likes = commentSelector.likes
    if (likes === undefined) {
        likes = []
    }
    if (comments === undefined) {
        comments = []
    }
    if (comments.length === 0) {
        return (
            <>No Comments yet</>
        )
    }
    else {
        return (
            <>
            {comments.map( (cmnt) => 
                <div key={cmnt.id} className={style.Comment} onClick = { () => {
                    if (indexOfLike === 0) {
                        setIndexOfLike(1)
                        dispatch(getLikesFromComment(cmnt.id))
                    }
                }}>
                    <a className={style.loginHome} onClick = {() => {history.push(`/user/${cmnt.user_id}`)}}>{cmnt.login}</a>
                    <p className={style.Commentcontent}>{cmnt.content}</p>
                    {user_id === cmnt.user_id ?
                        <>
                        <button onClick= { () => {
                            if (index === 0) {
                                setIndex(1)
                            }
                            else if (index === 1) {
                                setIndex(0)
                                dispatch(UpdateComment(cmnt.id,post_id, comment))
                            }
                            console.log(index);
                        }}>Edit</button>
                        {index === 1 ? <input type='text' placeholder="How u wanna edit comment" value={comment} onChange={e=>setComment(e.target.value)}/> :<></>}
                        <button onClick={() => {
                            dispatch(DeletComment(cmnt.id,post_id))
                        }}>Delete</button></>
                        :
                        <></>
                    }
                    <p className={style.Commenttime}>{moment(cmnt.time).fromNow()}</p>
                </div>

            )}
            </>
        )
    }
}

export default  CommentsForPost
