import React, {useEffect} from "react";
import { useDispatch } from "react-redux";
import { register } from "../../action/AuthAction";
import { useState } from "react";

const RegisterPage = () => {
    const dispatch = useDispatch()
    useEffect(()=> {
        dispatch(register())
    }, [])

    const [userData, setUserData] = useState({
        login:'',
        email:'',
        full_name:'',
        password:''
    })

    return (
        <>
            <h1>
                Register Form        
            </h1>
            <div>
                <input type="text" name="login" placeholder="Please paste your login"
                    maxLength={20} autoFocus required={true} value={userData.login} 
                    onChange = {(e)=>setUserData({login:e.target.value})}

                />
                <br></br>
                <br></br>

                <input type="email" name="email" placeholder="Please paste your email"
                    maxLength={20} autoFocus required={true} 
                    value = {userData.email} onChange= {(e)=>setUserData({email:e.target.value})}
                />
                <br></br>
                <br></br>
                <input type="text" name="full_name" placeholder="Please paste your Full name"
                    maxLength={20} autoFocus required={true} 
                    value = {userData.full_name} onChange= {() => setUserData({full_name: e.target.value})}
                />
                <br></br>
                <br></br>
                <input type="password" name="password" placeholder="Please paste your password"
                    minLength={8} autoFocus required={true} 
                /> 
                <br></br>
                <br></br>
                <div>
                    <a href="/login">Already have an account?</a>
                    <button>Register</button>
                </div>

            </div>
        </>

    );
}

export default RegisterPage;
