import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { createComment, GetLikes, getPostById } from "../../action/PostAction";
import { Link } from "react-router-dom";
import moment from 'moment'
import style from "../style/PostPage.module.css"
import GetLike from "./PostComponents/GetLikes";
import CommentsForPost from "./PostComponents/GetComment";

const PostPage = () => {
    const [comment, setComment] = useState('')
    const {id} = useParams()
    let post, likes, comments
    const dispatch = useDispatch()
    const data = useSelector(state => state.Post)
    console.log(data);
    useEffect ( () => {
        dispatch(getPostById(id))
    }, [dispatch])
    post = data.post
    likes = data.likes
    comments = data.comments

    const create = (e) => {
        e.preventDefault()
        console.log(comment);
        dispatch(createComment(id, comment))
    }
    if (comments === undefined) {
        comments = []
    }
    if (likes === undefined) {
        likes = []
    }
    if (post === undefined) {
        post = []
    }
    else {
        return (
            <>
                <div className={style.divAllPosts}>
                        <div className ={style.divPost} key={post[0].id}>
                            <Link className={style.loginHome} to ="/user/${pst.user_id}">{post[0].login}</Link>
                            <p className={style.titlePost}>Title: {post[0].title}</p>
                            <p className={style.contentPost}>{post[0].info}</p>
                            <GetLike id = {id} likes = {likes}/><button>Like</button>
                            <p className={style.timePost}>{moment(post[0].time).fromNow()}</p>
                        </div>
                        <div className={style.divComments}>
                            <p className={style.headerComments}>Comments</p>
                            <CommentsForPost post_id = {post[0].id} comments ={comments}/>
                            <input 
                                className={style.Commentinput}
                                name="comment" type='text' 
                                placeholder="Leave comment if you want" 
                                maxLength={255} 
                                value={comment} onChange = {e => setComment(e.target.value)}>
                            </input>
                            <button onClick={create}>Create comment</button>
                        </div>
                </div>
            </>
        );   
    }
}

export default PostPage;
