import axios from "axios";
import Cookies from "js-cookie";

const api = axios.create({
    baseURL:"http://127.0.0.1:3001/api"
})
const authIntreceptors = config => {
    config.headers.authorization = `Bearer ${Cookies.get('token')}` 
    return config
}
api.interceptors.request.use(authIntreceptors)

export const getPost = api.get("/posts/")
export const getLikesFromPost = (id) => api.get(`/posts/${id}/like`)
export const PostByid = (id) => api.get(`/posts/${id}`)
export const getCommentsOnPost = (id) => api.get(`/posts/${id}/comments`)
export const createCommentsOnPost = (id, comment) => api.post(`/posts/${id}/comments`, {comment})

export default api
