import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { getComments } from "../../../action/PostAction";


const CommentsForPost = (props) => {
    const {post_id,comments} = props
    const dispatch = useDispatch()
    useEffect ( () => {
        dispatch(getComments(post_id))
    }, [dispatch])
    if (comments === undefined) {
        comments = []
    }
    if (comments.length === 0) {
        return (
            <>No Comments yet</>
        )
    }
    else {
        return (
            <>
            {comments.map( (cmnt) => 
                <div key={cmnt.id}>
                    <p>{cmt.login}</p>
                    <p>{cmnt.content}</p>
                </div>
            )}
            </>
        )
    }
}

export default  CommentsForPost
