import React from "react";


const Pagination = ({postPerPage, totalPost, paginate}) => {
    const pageNumbers = []

    for (let index = 1; index <= Math.ceil(totalPost/postPerPage); index++) {
        pageNumbers.push(index)
    }

    return (
        <>
            <ul>
                {pageNumbers.map( number => (
                    <li key={number}>
                        <a onClick={ () => {
                            paginate(number)
                        }}>{number}</a>
                    </li>
                ))}
            </ul>
        </>
    )
}

export default Pagination
