import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { createPost, getPosts } from "../../action/PostAction";
import style from "../style/Homepage.module.css"
import moment from 'moment'
import { getCategories } from "../../action/CategoryAction";

const HomePage = () => {
    let posts  
    const history = useHistory()
    let [category, setCategory] = useState('')
    let [title, setTitle] = useState('')
    let [content, setContent] = useState('')
    const dispatch = useDispatch()

    const data = useSelector(state => state.Post)
    const auth = useSelector(store => store.Auth)
    const categorySelector = useSelector(store => store.Category)
    const token = auth.token

    useEffect(() => {
        dispatch(getPosts())
        dispatch(getCategories())
    },[dispatch])
    let categorie = categorySelector.categories
    console.log(categorie);
    posts = data.allPost
    if (posts === undefined) {
        posts = []
    }
    if (categorie.length === 0) {
        categorie = []
    }
    return (
        <>
            {token === '' ? 
                 <div className={style.divAllPosts}>
                    {posts.map((pst) => 
                        <div className ={style.divPost} key={pst.id}>
                            <a className={style.loginHome} onClick = {() => {history.push(`/register`)}}>{pst.login}</a>
                            <div className={style.divHref} onClick={()=>{history.push(`/register`)}}>
                            <p className={style.titlePost}>Title: {pst.title}</p>
                            <p className={style.contentPost}>{pst.info}</p>
                            <p className={style.timePost}>{moment(pst.time).fromNow()}</p>
                            </div>
                        </div>
                    )}
                </div>
                :
                <div className={style.divAllPosts}>
                    {posts.map((pst) => 
                        <div className ={style.divPost} key={pst.id}>
                            <a className={style.loginHome} onClick = {() => {history.push(`/user/${pst.user_id}`)}}>{pst.login}</a>
                            <div className={style.divHref} onClick={()=>{history.push(`/post/${pst.id}`)}}>
                            <p className={style.titlePost}>Title: {pst.title}</p>
                            <p className={style.contentPost}>{pst.info}</p>
                            <p className={style.timePost}>{moment(pst.time).fromNow()}</p>
                            </div>
                        </div>
                    )}
                    <div>
                        <select onChange={ e => setCategory(e.target.value)}>
                            {categorie.map( (categor) => 
                                <option value={category.title} key={categor.id}>{categor.title}</option>
                            )}
                        </select>
                        <input type='text' name="title" 
                        placeholder="Enter title of your post" autoFocus
                        value={title} onChange={e => setTitle(e.target.value)}
                        >    
                        </input>
                        <input type='text' name="content" 
                        placeholder="Enter description on your post" autoFocus 
                        value={content} onChange={e => setContent(e.target.value)}>
                        </input>
                        <button onClick={ () => {dispatch(createPost(title, content, category))}}>Create post</button>
                    </div>

                </div>
            }
        </>
    )
}

export default HomePage
