import React, { useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const DayPage = () => {
    const {day, calendar_id} = useParams()
    const event = useSelector(state => state.Event)


    console.log(calendar_id);
    const time = []
    for (let index = 0; index < 24; index+=1) {
        for (let i = 0; i < 60; i+=15) {
            if (index < 10 ) {
                if (i === 0) {
                    time.push(`0${index}:0${i}`)    
                }
                else {
                    time.push(`0${index}:${i}`)
                }
            } 

            else {
                if (i === 0) {
                    time.push(`${index}:0${i}`)
                }
                else {
                    time.push(`${index}:${i}`)
                }
            }
        }
    }
    const [value_of_date, setValue] = useState([])
    for (let index = 0; index < 96; index++) {
        value_of_date.push(`${day} ${time[index]}:00`)

    }
    console.log(value_of_date);
    return(
        <>
            <ul>
                {time.map( (date, index) => {
                    return (<li value={value_of_date[index]} key={value_of_date[index]}>{date}</li>)
                })}
            </ul>
        </>
    )
}

export default DayPage
