export const initialState = {
    allPost:[],
    likes: [],
    post : [],
    comments: [],
    category: []
}

export const PostReducer = (post = [],action) => {
    switch (action.type) {
        case 'getposts':
            action.payload.sort( (a, b) => {
                if (b.time < a.time) {
                    return -1
                }
                else {
                    return 1
                }
            })    
            return {...post, allPost:action.payload}

        case 'getPostsbyCategory':
            return {...post, allPost:action.payload}
        case `getlikes`:
            return {...post, likes:action.payload}
        case 'getpostbyid':
            return {...post, post:action.payload}
        case `getCommentsOnPost`:
            return {...post, comments:action.payload}
        case 'getCategory':
            return {...post, category:action.payload}
        case 'createcommentforpost': 
            return {...post, comments:action.payload}
        case 'createpost':
            return {...post, allPost: action.payload}
        case 'likepost':
            return {...post, likes: action.payload}

        case 'deletelike':
            return {...post, likes:action.payload}
        case 'deletecomment': 
            return {...post, comments: action.payload}
        case 'deletepost':
            return {...post, allPost:action.payload}

        case 'sortbyauthor':
            action.payload.sort((a, b) => {
                if (b.login > a.login) {
                    return -1
                } else {
                    return 1
                }
            })
            return { ...post, allPost: action.payload }
        case 'sortbyold':
            action.payload.sort( (a, b) => {
                if (b.time > a.time) {
                    return -1
                }
                else {
                    return 1
                }
            })    
            return {...post, allPost:action.payload}

        case 'updatecomment':
            return {...post, comments:action.payload}
        case 'updatepost':
            return {...post, post:action.payload}
        default:
            return post
    }
}
