import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { GetLikes, getPosts } from "../../action/PostAction";
import style from "../style/Homepage.module.css"
import GetLike from "./PostComponents/GetLikes";
import Post from "./PostComponents/Post";

const HomePage = () => {
    let [posts, setPost] = useState()    
    let [likes,setLike] = useState(1)
    const dispatch = useDispatch()
    const data = useSelector(state => state.Post)
    console.log(data);
    useEffect (() => {
        dispatch(getPosts())
    },[dispatch])
    posts = data.allPost
    if (posts === undefined) {
        posts = []
    }
    console.log(posts);
    let like 
    like = data.likes
    return (
        <>
            <div className={style.divAllPosts}>
                {posts.map((pst) =>
                    <Post post_id = {pst.id} title= {pst.title} info = {pst.info} like= {like} time={pst.time} user_login = {pst.login} />
                )}

            </div>
        </>
    )
}
export default HomePage
