import React, { useEffect, useState } from "react";
import moment from 'moment'
import { useDispatch, useSelector } from "react-redux";

import { getAllEventByCalendar } from "../../action/EventAction";
import MonthComponent from "./CalendarComponents/MonthComponent";
import EventComponent from "./CalendarComponents/EventCalendar";

import style from '../style/CalendarStyle.module.css'

const HomePage = () => {
    const event = useSelector(state => state.Event)
    moment.updateLocale(moment.locale(),{week:{dow:1}})

    const [now, setNow] = useState(moment())
    const [months, setMonths] = useState(now.month())
    const [year, setYear] = useState(now.year())

    const dispatch = useDispatch()
    useEffect ( () => {
        dispatch(getAllEventByCalendar(2))
    }, [dispatch])

    let AllEvents = []
    AllEvents = event.events
    if (AllEvents === undefined) {
        AllEvents = []
    }

    return (
        <>
            <div className={style.container_of_Calendar}>
                <MonthComponent 
                    setMonths ={setMonths}
                    setNow = {setNow}
                    setYear = {setYear}
                    now = {now}

                    months = {months}
                    year = {year}
                />
                <EventComponent 
                    now = {now}
                    AllEvents = {AllEvents}
                />
            </div>  
        </>
    )
}

export default HomePage;
