import { getUserById, updateinfo } from "../api/UserApi"

export const getUser = (id) => async(dispatch) => {
    try {
        const {data} = await getUserById(id)
        return dispatch({type:'getUser', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const UpdateUser = (id,login,email,full_name,password,avatar,role) => async(dispatch) => {
