import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getUser_by_id } from "../../../action/AuthAction";

const User = (props) => {
    const {id} = props
    const dispatch = useDispatch()
    const data = useSelector(state => state.Auth)
    useEffect( () => {  
        dispatch(getUser_by_id(id))
    },[dispatch])
    let user
    if (user === undefined) {
        user = []
    }
    user = data
    console.log(user.login);    
    return (
        <>

        {user.map( (usr) => 
            <div>{usr.login}</div>    
        )}
        </>
    )

}

export default User;
