import React, { useState } from "react";
import dayjs from 'dayjs';
import moment from 'moment'

const HomePage = () => {
    moment.updateLocale(moment.locale(),{week:{dow:1}})
    const [now, setNow] = useState(moment())
    const [months, setMonths] = useState(now.month())
    const [year, setYear] = useState(now.year())
    console.log(months);
    const startDay = moment().startOf('month').startOf('week');
    const lastday = moment().endOf('month').endOf('week');
    const calendar = []
    const day = startDay.clone()
    while (!day.isAfter(lastday)) {
        calendar.push(day.clone())
        day.add(1, 'day')
    }
    console.log(calendar);


    const month = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November','December']

    console.log(now);
    return (
        <>
            <div>
                <p><button onClick={ () => {
                    if (months === 1) {
                        setNow(now.subtract(1,"year"))
                        setMonths(now.month())
                        setYear(now.year())
                    }
                    else {
                        setNow(now.subtract(1,'month'))
                        setYear(now.year())
                        setMonths(now.month())
                    }
                    }}>
                    Prev</button>{month[now.month()] +year} 
                    <button onClick={ () => {
                    if (months === 12) {
                        setNow(now.add(1,"year"))
                        setYear(now.year())
                    }
                    else {
                        setNow(now.add(1,'month'))
                        setYear(now.year())
                        setMonths(now.month())
                    }
                    }}>Next</button> </p>
                    <ul>

                    </ul>
            </div>  
        </>
    )
}

export default HomePage;
