import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { GetLikes, getPosts } from "../../action/PostAction";
import style from "../style/Homepage.module.css"
import GetLike from "./PostComponents/GetLikes";

const HomePage = () => {
    let [posts, setPost] = useState()    
    let [likes,setLike] = useState(1)
    const dispatch = useDispatch()
    const data = useSelector(state => state.Post)
    useEffect (() => {
        dispatch(getPosts())
    },[dispatch])
    posts = data.allPost
    if (posts === undefined) {
        posts = []
    }
    const like = data.likes
    console.log(data);
    return (
        <>
            <div className={style.divAllPosts}>
                {posts.forEach((pst) =>
                    {<div className ={style.divPost} key={pst.id}>
                        <Link className={style.loginHome} to ="/user/${pst.user_id}">{pst.login}</Link>
                        <p className={style.titlePost}>Title: {pst.title}</p>
                        <p className={style.contentPost}>{pst.info}</p>
                        <p className={style.timePost}>{pst.time.substring(0,10)}</p>
                        <div><GetLike id = {pst.id} likes = {like}/></div><button className={style.buttonLikePost}>LIKE</button>
                        <button className={style.buttonCommentPost}>COMMENTS</button>
                    </div>}
                )}

            </div>
        </>
    )
}
export default HomePage
