import { getCategory } from "../api/CategoryApi"

export const getCategories = () => async(dispatch) => {
    try {
        const {data} = await getCategory()
        return dispatch({type:'getCategory', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}
