export const initialState = {
    allPost:[],
    likes: [],
    post : [],
    comments: [],
    pasteLike:false 
}

export const PostReducer = (post = [],action) => {
    switch (action.type) {
        case 'getposts':
            return {...post,allPost:action.payload}
        case `getlikes`:
            return {...post,likes:action.payload}
        case 'getpostbyid':
            return {...post,post:action.payload}
        case `getCommentsOnPost`:
            return {...post, comments:action.payload}
        case 'createcommentforpost': 
            return {...post, comments: post.comments.concat({...action.payload})}
        case 'likepost':
            return {...post, likes: post.likes.concat( {...action.payload} ), pasteLike:true}
        case 'deletelike':
            return {...post, likes:action.payload, pasteLike:false}
        case 'deletecomment': 
            return {...post, comments: action.payload}
        case 'deletepost':
            return {...post, allPost:action.payload}
        case 'updatecomment':
            return {...post, comments:action.payload}
        default:
            return post
    }
}
