import axios from "axios";
import Cookies from "js-cookie";

const api = axios.create({
    baseURL:"http://127.0.0.1:3001/api"
})
const authIntreceptors = config => {
    config.headers.authorization = `Bearer ${Cookies.get('token')}` 
    return config
}
api.interceptors.request.use(authIntreceptors)

export const getUserById = (id) => api.get(`/users/${id}`)
export const updateinfo = (id, login,email,full_name,password,avatar,role) => api.patch(`/users/${id}`, {
    login:login, full_name:full_name, email:email, role:role, password:password, avatar:avatar}
)
export const updateAva = (id, photo) => api.patch('/users/avatar', {id:id, photo:photo}, {
    headers: {
      'Content-Type': 'multipart/form-data'
    }}
    )
