import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import {Reset_Password } from "../../action/AuthAction";
import style from '../style/UserForm/ResetPassword.module.css'

const ResetPassword = () => {
    const history = useHistory()
    const dispatch = useDispatch()
    let [email, setEmail] = useState('')
    return (
        <div className={style.divResetPassword}>
            <h1>Reset password</h1>
            <p>E-mail: 
            <input autoFocus required 
                placeholder="Enter your email"
                value = {email} type="email"
                onChange={ e => setEmail(e.target.value)} />
            </p>
            <a onClick={()=>{history.push("/login")}}>Remember</a>
            <button onClick={ () => {dispatch(Reset_Password(email))}}>Reset Password</button>
        </div>
    )
}

export default ResetPassword
