import React, { useState } from "react";
import dayjs from 'dayjs';
import moment from 'moment'

const HomePage = () => {
    moment.updateLocale(moment.locale(),{week:{dow:1}})
    const [now, setNow] = useState(new Date())
    const startDay = moment().startOf('month').startOf('week');
    const lastday = moment().endOf('month').endOf('week');
    const calendar = []
    const day = startDay.clone()
    while (!day.isAfter(lastday)) {
        calendar.push(day.clone)
        day.add
    }

    const [months, setMonths] = useState(now.getMonth())
    const [lastDayOfMonth, setLastDayOfMonth] = useState((new Date(now.getFullYear(), months + 1, 0)).getDate())
    let days = []
    for (let index = 1; index <= lastDayOfMonth; index++) {
        days.push(index)
    }
    console.log(days);
    const month = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November','December']
    console.log(months);
    console.log(now);
    return (
        <>
            <div>
                <p><button onClick={ () => {
                    if (months === 0) {
                        setNow(new Date(new Date().setFullYear(now.getFullYear()-1)))
                        setMonths(11)
                    }
                    else {
                        setMonths(months-1)
                    }
                    }}>
                    Prev</button>{month[months] + now.getFullYear()} 
                    <button onClick={ () => {
                    if(months === 11) {
                        setNow(new Date().setMonth(0).setFullYear(now.getFullYear()+1))
                        setLastDayOfMonth((new Date(now.getFullYear(), months + 1, 0)).getDate())
                        setMonths(0)
                    }

                    else {
                        setNow(new Date(new Date().setMonth(now.getMonth()+1)))
                        setMonths(months + 1)
                    }
                    }}>Next</button> </p>
                    <ul>

                    </ul>
            </div>  
        </>
    )
}

export default HomePage;
