import { createCat, delCategory, getCategory } from "../api/CategoryApi"

export const getCategories = () => async(dispatch) => {
    try {
        const {data} = await getCategory()
        console.log(data);
        return dispatch({type:'getCategory', payload:data.result})
    } catch (error) {
        console.log(error);
    }
}

export const createCategory = (title) => async(dispatch) => {
    try {
        const {data} = await createCat(title) 
        if (data.result.affectedRows > 0) {
            const Data = await getCategory()
            return dispatch({type:'createCategory', payload:Data.data.result})
        }
    } catch (error) {
        console.log(error);
    }
}

export const deleteCategory = (id) => async(dispatch) => {
    try {
        const {data} = await delCategory(id)
        if (data.message === 'Category was deleted') {
            const Data = await getCategory()
            return dispatch({type:'deleteCategory', payload: Data.data.result})
        }
    } catch (error) {
        console.log(error);
    }
}
